import React, { useState } from 'react';
import axios from 'axios';
import { Instagram, Linkedin, Twitter, TrendingUp, Loader2, Users } from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [formData, setFormData] = useState({
    instagram_username: '',
    linkedin_username: '',
    twitter_username: ''
  });
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatFollowerCount = (count) => {
    if (typeof count !== 'number') return count;
    
    if (count >= 1000000) {
      return (count / 1000000).toFixed(1) + 'M';
    } else if (count >= 1000) {
      return (count / 1000).toFixed(1) + 'K';
    }
    return count.toLocaleString();
  };

  const getPlatformIcon = (platform) => {
    switch (platform?.toLowerCase()) {
      case 'instagram':
        return <Instagram size={20} />;
      case 'linkedin':
        return <Linkedin size={20} />;
      case 'x (twitter)':
      case 'twitter':
        return <Twitter size={20} />;
      default:
        return <Users size={20} />;
    }
  };

  const getPlatformClass = (platform) => {
    switch (platform?.toLowerCase()) {
      case 'instagram':
        return 'instagram';
      case 'linkedin':
        return 'linkedin';
      case 'x (twitter)':
      case 'twitter':
        return 'twitter';
      default:
        return 'default';
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResults(null);

    // Filter out empty usernames
    const requestData = {};
    if (formData.instagram_username.trim()) {
      requestData.instagram_username = formData.instagram_username.trim();
    }
    if (formData.linkedin_username.trim()) {
      requestData.linkedin_username = formData.linkedin_username.trim();
    }
    if (formData.twitter_username.trim()) {
      requestData.twitter_username = formData.twitter_username.trim();
    }

    if (Object.keys(requestData).length === 0) {
      setError('Please enter at least one username');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/followers`, requestData);
      setResults(response.data);
    } catch (err) {
      setError('Failed to fetch follower counts. Please check your usernames and try again.');
      console.error('API Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const renderResultCard = (platform, data) => {
    if (!data) return null;

    const hasError = data.error;
    
    return (
      <div key={platform} className={`result-card ${hasError ? 'error' : ''}`}>
        <div className="result-header">
          <div className={`platform-icon ${getPlatformClass(data.platform || platform)}`}>
            {getPlatformIcon(data.platform || platform)}
          </div>
          <div className="platform-name">
            {data.platform || platform}
          </div>
        </div>
        
        {hasError ? (
          <div className="error-message">{data.error}</div>
        ) : (
          <>
            <div className="username">@{data.username}</div>
            <div className="follower-count">
              {formatFollowerCount(data.followers)}
            </div>
            <div className="follower-label">Followers</div>
          </>
        )}
      </div>
    );
  };

  return (
    <div className="container">
      <div className="card">
        <div className="header">
          <h1 className="title">
            <TrendingUp className="inline-icon" />
            Social Media Tracker
          </h1>
          <p className="subtitle">
            Enter your usernames to get follower counts from Instagram, LinkedIn, and X (Twitter)
          </p>
        </div>

        <form onSubmit={handleSubmit} className="form">
          <div className="input-group">
            <label className="input-label">
              <Instagram size={20} />
              Instagram Username
            </label>
            <input
              type="text"
              name="instagram_username"
              value={formData.instagram_username}
              onChange={handleInputChange}
              className="input"
              placeholder="username"
            />
          </div>

          <div className="input-group">
            <label className="input-label">
              <Linkedin size={20} />
              LinkedIn Username
            </label>
            <input
              type="text"
              name="linkedin_username"
              value={formData.linkedin_username}
              onChange={handleInputChange}
              className="input"
              placeholder="username"
            />
          </div>

          <div className="input-group">
            <label className="input-label">
              <Twitter size={20} />
              X (Twitter) Username
            </label>
            <input
              type="text"
              name="twitter_username"
              value={formData.twitter_username}
              onChange={handleInputChange}
              className="input"
              placeholder="username"
            />
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className="submit-btn"
          >
            {loading ? (
              <>
                <Loader2 className="loading-spinner" />
                Getting Follower Counts...
              </>
            ) : (
              <>
                <TrendingUp size={20} />
                Get Follower Counts
              </>
            )}
          </button>
        </form>

        {results && (
          <div className="results">
            <h2 className="results-title">Your Follower Counts</h2>
            <div className="results-grid">
              {results.instagram && renderResultCard('Instagram', results.instagram)}
              {results.linkedin && renderResultCard('LinkedIn', results.linkedin)}
              {results.twitter && renderResultCard('X (Twitter)', results.twitter)}
            </div>
          </div>
        )}

        {results && !results.instagram && !results.linkedin && !results.twitter && (
          <div className="empty-state">
            No results to display. Please check your usernames and try again.
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
