import axios from 'axios';
import { Instagram, Linkedin, Twitter, TrendingUp, Loader2, RefreshCw, Users, BarChart3, Sparkles } from 'lucide-react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import './App.css';

ChartJS.register(ArcElement, Tooltip, Legend);

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [formData, setFormData] = useState({
    instagram_username: '',
    linkedin_username: '',
    twitter_username: ''
  });
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResults(null);

    // Filter out empty usernames
    const requestData = {};
    if (formData.instagram_username.trim()) {
      requestData.instagram_username = formData.instagram_username.trim();
    }
    if (formData.linkedin_username.trim()) {
      requestData.linkedin_username = formData.linkedin_username.trim();
    }
    if (formData.twitter_username.trim()) {
      requestData.twitter_username = formData.twitter_username.trim();
    }

    if (Object.keys(requestData).length === 0) {
      setError('Please enter at least one username');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/followers`, requestData);
      setResults(response.data);
    } catch (err) {
      setError('Failed to fetch follower counts. Please check your usernames and try again.');
      console.error('API Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = () => {
    if (results) {
      handleSubmit({ preventDefault: () => {} });
    }
  };

  const calculateTotalFollowers = () => {
    if (!results) return 0;
    let total = 0;
    
    if (results.instagram && !results.instagram.error) {
      total += parseInt(results.instagram.followers) || 0;
    }
    if (results.linkedin && !results.linkedin.error) {
      total += parseInt(results.linkedin.followers) || 0;
    }
    if (results.twitter && !results.twitter.error) {
      total += parseInt(results.twitter.followers) || 0;
    }
    
    return total;
  };

  const getChartData = () => {
    if (!results) return null;
    
    const data = [];
    const labels = [];
    const colors = [];
    
    if (results.instagram && !results.instagram.error) {
      const followers = parseInt(results.instagram.followers) || 0;
      if (followers > 0) {
        data.push(followers);
        labels.push('Instagram');
        colors.push('#E4405F');
      }
    }
    
    if (results.linkedin && !results.linkedin.error) {
      const followers = parseInt(results.linkedin.followers) || 0;
      if (followers > 0) {
        data.push(followers);
        labels.push('LinkedIn');
        colors.push('#0077B5');
      }
    }
    
    if (results.twitter && !results.twitter.error) {
      const followers = parseInt(results.twitter.followers) || 0;
      if (followers > 0) {
        data.push(followers);
        labels.push('X (Twitter)');
        colors.push('#000000');
      }
    }
    
    if (data.length === 0) return null;
    
    return {
      labels,
      datasets: [{
        data,
        backgroundColor: colors,
        borderColor: colors.map(color => color + '20'),
        borderWidth: 2,
      }]
    };
  };

  const formatNumber = (num) => {
    if (num >= 1000000) {
      return (num / 1000000).toFixed(1) + 'M';
    } else if (num >= 1000) {
      return (num / 1000).toFixed(1) + 'K';
    }
    return num.toString();
  };

  const renderResultCard = (platform, data) => {
    if (data.error) {
      return (
        <div key={platform} className="result-card error-card">
          <div className="result-header">
            <div className="platform-info">
              {platform === 'Instagram' && <Instagram className="platform-icon instagram" />}
              {platform === 'LinkedIn' && <Linkedin className="platform-icon linkedin" />}
              {platform === 'X (Twitter)' && <Twitter className="platform-icon twitter" />}
              <span className="platform-name">{platform}</span>
            </div>
          </div>
          <div className="error-text">{data.error}</div>
        </div>
      );
    }

    return (
      <div key={platform} className="result-card success-card">
        <div className="result-header">
          <div className="platform-info">
            {platform === 'Instagram' && <Instagram className="platform-icon instagram" />}
            {platform === 'LinkedIn' && <Linkedin className="platform-icon linkedin" />}
            {platform === 'X (Twitter)' && <Twitter className="platform-icon twitter" />}
            <span className="platform-name">{platform}</span>
          </div>
        </div>
        <div className="follower-count">{formatNumber(parseInt(data.followers))}</div>
        <div className="follower-label">followers</div>
        <div className="username">@{data.username}</div>
      </div>
    );
  };

  const chartData = getChartData();
  const totalFollowers = calculateTotalFollowers();

  return (
    <div className="app">
      {/* Hero Section */}
      <div className="hero-section">
        <div className="hero-content">
          <div className="hero-badge">
            <Sparkles size={16} />
            <span>Social Media Analytics</span>
          </div>
          <h1 className="hero-title">
            Track Your Social Media
            <span className="gradient-text"> Followers</span>
          </h1>
          <p className="hero-subtitle">
            Get real-time follower counts from Instagram, LinkedIn, and X (Twitter) all in one place.
            Simply enter your usernames and discover your social reach.
          </p>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="card form-card">
          <div className="form-header">
            <h2>Enter Your Social Media Usernames</h2>
            <p>We'll fetch your current follower counts across all platforms</p>
          </div>

          <form onSubmit={handleSubmit} className="form">
            <div className="input-grid">
              <div className="input-group">
                <label className="input-label">
                  <Instagram size={20} />
                  Instagram Username
                </label>
                <input
                  type="text"
                  name="instagram_username"
                  value={formData.instagram_username}
                  onChange={handleInputChange}
                  className="input"
                  placeholder="username"
                />
              </div>

              <div className="input-group">
                <label className="input-label">
                  <Linkedin size={20} />
                  LinkedIn Username
                </label>
                <input
                  type="text"
                  name="linkedin_username"
                  value={formData.linkedin_username}
                  onChange={handleInputChange}
                  className="input"
                  placeholder="username"
                />
              </div>

              <div className="input-group">
                <label className="input-label">
                  <Twitter size={20} />
                  X (Twitter) Username
                </label>
                <input
                  type="text"
                  name="twitter_username"
                  value={formData.twitter_username}
                  onChange={handleInputChange}
                  className="input"
                  placeholder="username"
                />
              </div>
            </div>

            {error && (
              <div className="error-message">
                {error}
              </div>
            )}

            <div className="button-group">
              <button
                type="submit"
                disabled={loading}
                className="submit-btn primary"
              >
                {loading ? (
                  <>
                    <Loader2 className="loading-spinner" />
                    Fetching Data...
                  </>
                ) : (
                  <>
                    <TrendingUp size={20} />
                    Get Follower Counts
                  </>
                )}
              </button>

              {results && (
                <button
                  type="button"
                  onClick={handleRefresh}
                  disabled={loading}
                  className="refresh-btn secondary"
                >
                  <RefreshCw size={18} />
                  Refresh
                </button>
              )}
            </div>
          </form>
        </div>

        {results && (
          <>
            {/* Stats Overview */}
            <div className="stats-section">
              <div className="stats-card total-followers">
                <div className="stat-icon">
                  <Users size={24} />
                </div>
                <div className="stat-content">
                  <div className="stat-number">{formatNumber(totalFollowers)}</div>
                  <div className="stat-label">Total Followers</div>
                </div>
              </div>

              {chartData && (
                <div className="stats-card chart-card">
                  <div className="chart-header">
                    <div className="stat-icon">
                      <BarChart3 size={24} />
                    </div>
                    <div>
                      <div className="chart-title">Follower Distribution</div>
                      <div className="chart-subtitle">Across all platforms</div>
                    </div>
                  </div>
                  <div className="chart-container">
                    <Pie 
                      data={chartData} 
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        plugins: {
                          legend: {
                            position: 'bottom',
                            labels: {
                              padding: 20,
                              usePointStyle: true,
                              font: {
                                size: 12
                              }
                            }
                          },
                          tooltip: {
                            callbacks: {
                              label: function(context) {
                                const total = context.dataset.data.reduce((a, b) => a + b, 0);
                                const percentage = ((context.raw / total) * 100).toFixed(1);
                                return `${context.label}: ${formatNumber(context.raw)} (${percentage}%)`;
                              }
                            }
                          }
                        }
                      }}
                    />
                  </div>
                </div>
              )}
            </div>

            {/* Results Grid */}
            <div className="results-section">
              <div className="results-header">
                <h2>Your Social Media Stats</h2>
                <p>Here's a breakdown of your follower counts across all platforms</p>
              </div>
              <div className="results-grid">
                {results.instagram && renderResultCard('Instagram', results.instagram)}
                {results.linkedin && renderResultCard('LinkedIn', results.linkedin)}
                {results.twitter && renderResultCard('X (Twitter)', results.twitter)}
              </div>
            </div>
          </>
        )}

        {results && !results.instagram && !results.linkedin && !results.twitter && (
          <div className="empty-state">
            <div className="empty-icon">
              <TrendingUp size={48} />
            </div>
            <h3>No results to display</h3>
            <p>Please check your usernames and try again.</p>
          </div>
        )}
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <p>&copy; 2024 Social Media Follower Counter. Track your social presence with ease.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
